t;System.out.println(10 % 3);//1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;System.out.println(-10 % 3);//-1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;System.out.println(10 % -3);//1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;System.out.println(-10 % -3);//-1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;//++  --&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;//独立语句使用时，前++和后++一样&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;int i = 10;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;i++;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;System.out.println(&amp;quot;i=&amp;quot; + i);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;int j = 8;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;int k = ++j; // j = j + 1  k = j  j = 9  k = 9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;int f = j++;// f = j j = j + 1  f = 9 j =10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;System.out.println(&amp;quot;j=&amp;quot; + j + &amp;quot;k=&amp;quot; + k + &amp;quot;f=&amp;quot; + f);&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;ul&gt;
&lt;li&gt;面试题&lt;/li&gt;
&lt;/ul&gt;
&lt;figure class=&#34;highlight plaintext&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;int i = 1;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;i = i++;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;System.out.println(i);&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;p&gt;规则（使用临时变量）：（1）temp = i  (2) i = i+1  (3) i = temp&lt;br&gt;
 &lt;code&gt;输出结果为1&lt;/code&gt;&lt;/p&gt;
&lt;figure class=&#34;highlight plaintext&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;int i= 1;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;i = ++i;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;System.out.println(i);&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;p&gt;规则（使用临时变量）：(1) i = i+1 （2）temp = i  (3) i = temp&lt;br&gt;
 &lt;code&gt;输出结果为2&lt;/code&gt;&lt;/p&gt;
